import React, { useState } from 'react';
import { Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, TextField } from '@mui/material'; // Импорт компонентов Material-UI
import '../styles/modal.css'; // Импорт файла стилей для модального окна

function EmployeeForm({ onCloseModal, onAddEmployee }) {
    const [employee, setEmployee] = useState({ name: '', position: '', access: '', birthdate: '', phone: '', address: '' });
    const [open, setOpen] = useState(false); // Состояние для открытия и закрытия модального окна

    const handleSubmit = (e) => {
        e.preventDefault();
        onAddEmployee(employee);
        onCloseModal();
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEmployee({ ...employee, [name]: value });
    };

    return (
        <Dialog open={open} onClose={onCloseModal}>
            <DialogTitle>Добавление сотрудника</DialogTitle>
            <DialogContent>
                <DialogContentText>
                    Заполните данные сотрудника:
                </DialogContentText>
                <form onSubmit={handleSubmit}>
                    <TextField
                        autoFocus
                        margin="dense"
                        name="name"
                        label="Имя"
                        type="text"
                        fullWidth
                        value={employee.name}
                        onChange={handleChange}
                    />
                    <TextField
                        margin="dense"
                        name="position"
                        label="Должность"
                        type="text"
                        fullWidth
                        value={employee.position}
                        onChange={handleChange}
                    />
                    <TextField
                        margin="dense"
                        name="access"
                        label="Допуск"
                        type="text"
                        fullWidth
                        value={employee.access}
                        onChange={handleChange}
                    />
                    <TextField
                        margin="dense"
                        name="birthdate"
                        label="День рождения"
                        type="text"
                        fullWidth
                        value={employee.birthdate}
                        onChange={handleChange}
                    />
                    <TextField
                        margin="dense"
                        name="phone"
                        label="Телефон"
                        type="text"
                        fullWidth
                        value={employee.phone}
                        onChange={handleChange}
                    />
                    <TextField
                        margin="dense"
                        name="address"
                        label="Адрес"
                        type="text"
                        fullWidth
                        value={employee.address}
                        onChange={handleChange}
                    />
                    <DialogActions>
                        <Button onClick={onCloseModal} color="primary">
                            Отмена
                        </Button>
                        <Button type="submit" color="primary">
                            Добавить
                        </Button>
                    </DialogActions>
                </form>
            </DialogContent>
        </Dialog>
    );
}

export default EmployeeForm;
