import React, { useState, useEffect } from 'react';
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';
import VisibilityIcon from '@mui/icons-material/Visibility';
import TextField from '@mui/material/TextField';
import data from '../data.json';
import '../styles/accounting.css';

function Accounting() {
    const [employees, setEmployees] = useState(data);
    const [filteredEmployees, setFilteredEmployees] = useState([]);
    const [searchInput, setSearchInput] = useState('');
    const [isButtonVisible, setIsButtonVisible] = useState(true);
    const [isAnyModalOpen, setIsAnyModalOpen] = useState(false);

    useEffect(() => {
        // Фильтрация сотрудников по введенному значению в поле поиска
        const filtered = employees.filter(employee =>
            employee.name.toLowerCase().includes(searchInput.toLowerCase())
        );
        setFilteredEmployees(filtered);
    }, [searchInput, employees]);

    // Функция для удаления сотрудника
    const handleDeleteEmployee = (employee) => {
        const updatedEmployees = employees.filter(emp => emp.id !== employee.id);
        setEmployees(updatedEmployees);
    };

    // Функция для сортировки сотрудников по имени
    const handleSortByName = () => {
        const sortedEmployees = [...filteredEmployees].sort((a, b) => a.name.localeCompare(b.name));
        setFilteredEmployees(sortedEmployees);
    };

    return (
        <div>
            <div style={{ marginBottom: '10px' }}>
                <TextField
                    id="search"
                    label="Поиск"
                    variant="outlined"
                    value={searchInput}
                    onChange={(e) => setSearchInput(e.target.value)}
                />
            </div>

            <table className="employee-table">
                <thead>
                    <tr>
                        <th>Имя</th>
                        <th>Должность</th>
                        <th>Допуск</th>
                        <th>Действие</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredEmployees.map((employee, index) => (
                        <tr key={index}>
                            <td>{employee.name}</td>
                            <td>{employee.position}</td>
                            <td>{employee.access}</td>
                            <td>
                                <IconButton aria-label="details" disabled={isAnyModalOpen}>
                                    <VisibilityIcon />
                                </IconButton>
                                <IconButton color="primary" aria-label="edit" disabled={isAnyModalOpen}>
                                    <EditIcon />
                                </IconButton>
                                <IconButton color="secondary" aria-label="delete" onClick={() => handleDeleteEmployee(employee)} disabled={isAnyModalOpen}>
                                    <DeleteIcon />
                                </IconButton>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <div className="containeradd text-center">
                {isButtonVisible && !isAnyModalOpen && (
                    <Fab color="primary" aria-label="add">
                        <AddIcon />
                    </Fab>
                )}
            </div>
        </div>
    );
}

export default Accounting;
