import React from 'react';
import Button from '@mui/material/Button'; 
import Box from '@mui/material/Box'; 
import { makeStyles } from '@mui/styles'; // Импорт makeStyles

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        textAlign: 'center', 
    },
    title: {
        flexGrow: 1,
    },
}));

function EmployeeDelete({ employee, onDeleteEmployee, onCloseModal }) {
    const classes = useStyles();

    const handleDelete = () => {
        onDeleteEmployee(employee);
        onCloseModal();
    };

    return (
        <div className="modal">
            <div className="modal-content">
                <h2>Удаление сотрудника</h2>
                <p>Вы уверены, что хотите удалить сотрудника {employee.name}?</p>
                <Box sx={{ display: 'flex', justifyContent: 'center', gap: '28px' }}>
                    <Button variant="contained" color="error" onClick={handleDelete}>
                        Удалить
                    </Button>
                    <Button variant="contained" onClick={onCloseModal}>
                        Отмена
                    </Button>
                </Box>
            </div>
        </div>
    );
}

export default EmployeeDelete;
