// Импорт компонентов React и useState из библиотеки React
import React, { useState } from 'react';
// Импорт компонентов Material-UI
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';
import VisibilityIcon from '@mui/icons-material/Visibility';
// Импорт компонентов для модальных окон и данных
import EmployeeForm from './employeeForm';
import EmployeeEdit from './employeeEdit';
import EmployeeDetails from './employeeFormDetails';
import EmployeeDelete from './employeeDelete';
import Popup from './Popup'; 
import data from '../data.json';
import '../styles/accounting.css';

// Компонент Accounting
function Accounting() {
    // Состояния для управления модальными окнами и данными сотрудников
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
    const [isDetailsOpen, setIsDetailsOpen] = useState(false);
    const [isButtonVisible, setIsButtonVisible] = useState(true); 
    const [employees, setEmployees] = useState(data);
    const [selectedEmployee, setSelectedEmployee] = useState(null); 
    const [isAnyModalOpen, setIsAnyModalOpen] = useState(false); 

    // Функция для открытия модального окна добавления сотрудника
    const handleOpenModal = () => {
        setIsModalOpen(true);
        setIsAnyModalOpen(true); 
        setIsButtonVisible(false); 
    };

    // Функция для закрытия модального окна добавления сотрудника
    const handleCloseModal = () => {
        setIsModalOpen(false);
        setSelectedEmployee(null);
        setIsAnyModalOpen(isEditModalOpen || isDeleteModalOpen || isDetailsOpen);
        setIsButtonVisible(true); 
    };

    // Функция для открытия модального окна редактирования сотрудника
    const handleEditModalOpen = () => {
        setIsEditModalOpen(true);
        setIsAnyModalOpen(true); 
    };

    // Функция для закрытия модального окна редактирования сотрудника
    const handleEditModalClose = () => {
        setIsEditModalOpen(false);
        setSelectedEmployee(null);
        setIsAnyModalOpen(isModalOpen || isDeleteModalOpen || isDetailsOpen);
    };

    // Функция для добавления нового сотрудника
    const handleAddEmployee = (employeeData) => {
        setEmployees([...employees, employeeData]);
        handleCloseModal();
    };

    // Функция для обновления данных существующего сотрудника
    const handleUpdateEmployee = (employeeData) => {
        const updatedEmployees = employees.map(employee => {
            if (employee.id === employeeData.id) {
                return employeeData;
            }
            return employee;
        });
        setEmployees(updatedEmployees);
        handleEditModalClose();
    };

    // Функция для открытия модального окна редактирования сотрудника
    const handleEditEmployee = (employee) => {
        setSelectedEmployee(employee); 
        handleEditModalOpen();
    };

    // Функция для открытия модального окна удаления сотрудника
    const handleDeleteEmployee = (employee) => {
        setSelectedEmployee(employee); 
        setIsDeleteModalOpen(true);
        setIsAnyModalOpen(true); 
    };

    // Функция для подтверждения удаления сотрудника
    const handleConfirmDelete = () => {
        const updatedEmployees = employees.filter(emp => emp.id !== selectedEmployee.id);
        setEmployees(updatedEmployees);
        setIsDeleteModalOpen(false);
        setSelectedEmployee(null);
        setIsAnyModalOpen(isModalOpen || isEditModalOpen || isDetailsOpen);
    };

    // Функция для отображения деталей сотрудника
    const handleShowDetails = (employee) => {
        setSelectedEmployee(employee); 
        setIsDetailsOpen(true);
        setIsAnyModalOpen(true); 
    };

    // Функция для закрытия модального окна деталей сотрудника
    const handleCloseDetails = () => {
        setIsDetailsOpen(false);
        setSelectedEmployee(null);
        setIsAnyModalOpen(isModalOpen || isEditModalOpen || isDeleteModalOpen);
    };

    // В родительском компоненте Accounting


    return (
        <div>
            <div style={{ marginBottom: '10px' }}>
            </div>

            {isModalOpen && (
                <div className="modal-overlay">
                    <EmployeeForm onCloseModal={handleCloseModal} onAddEmployee={handleAddEmployee} />
                </div>
            )}

            {isEditModalOpen && selectedEmployee && (
                <div className="modal-overlay">
                    <EmployeeEdit
                        employeeData={selectedEmployee}
                        onUpdateEmployee={handleUpdateEmployee}
                        onCloseModal={handleEditModalClose}
                    />
                </div>
            )}

            {isDeleteModalOpen && selectedEmployee && (
                <div className="modal-overlay">
                    <EmployeeDelete
                        employee={selectedEmployee}
                        onDeleteEmployee={handleConfirmDelete}
                        onCloseModal={() => setIsDeleteModalOpen(false)}
                    />
                </div>
            )}

            {isDetailsOpen && selectedEmployee && (
                <div className="modal-overlay">
                    <Popup isOpen={isDetailsOpen} onClose={handleCloseDetails}>
                        <EmployeeDetails employee={selectedEmployee} />
                    </Popup>
                </div>
            )}

            <table className="employee-table">
                <thead>
                    <tr>
                        <th>Имя</th>
                        <th>Должность</th>
                        <th>Допуск</th>
                        <th>Действие</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((employee, index) => (
                        <tr key={index}>
                            <td>{employee.name}</td>
                            <td>{employee.position}</td>
                            <td>{employee.access}</td>
                            <td>
                                <IconButton aria-label="details" onClick={() => handleShowDetails(employee)} disabled={isAnyModalOpen}>
                                    <VisibilityIcon />
                                </IconButton>
                                <IconButton color="primary" aria-label="edit" onClick={() => handleEditEmployee(employee)} disabled={isAnyModalOpen}>
                                    <EditIcon />
                                </IconButton>
                                <IconButton color="secondary" aria-label="delete" onClick={() => handleDeleteEmployee(employee)} disabled={isAnyModalOpen}>
    <DeleteIcon />
</IconButton>

                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <div className="containeradd text-center">
                {isButtonVisible && !isAnyModalOpen && (
                    <Fab color="primary" aria-label="add" onClick={handleOpenModal}>
                        <AddIcon />
                    </Fab>
                )}
            </div>
        </div>
    );
}

export default Accounting;
