import React, { useState } from 'react';
import EmployeeForm from './employeeForm'; // Путь к компоненту формы добавления сотрудника

function Accounting() {
    const [isFormVisible, setIsFormVisible] = useState(false); // Состояние видимости формы добавления сотрудника

    // Функция для открытия формы добавления сотрудника
    const handleOpenForm = () => {
        setIsFormVisible(true);
    };

    // Функция для закрытия формы добавления сотрудника
    const handleCloseForm = () => {
        setIsFormVisible(false);
    };

    return (
        <div>
            {/* Кнопка для открытия формы добавления сотрудника */}
            <button onClick={handleOpenForm}>Добавить сотрудника</button>

            {/* Условный рендеринг: если состояние isFormVisible равно true, отображаем форму */}
            {isFormVisible && <EmployeeForm onCloseForm={handleCloseForm} />}

            {/* Остальной код компонента Accounting */}
        </div>
    );
}

export default Accounting;
