import React, { useState } from 'react';
import EmployeeForm from './employeeForm';
import EmployeeEdit from './employeeEdit';
import EmployeeDetails from './employeeFormDetails';
import EmployeeDelete from './employeeDelete';
import Popup from './Popup'; // Импортируем компонент Popup
import data from '../data.json';
import '../styles/accounting.css';
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';

function Accounting() {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalContent, setModalContent] = useState(null); // Содержимое модального окна
    const [employees, setEmployees] = useState(data);
    const [selectedEmployee, setSelectedEmployee] = useState(null); // Состояние для хранения выбранного сотрудника

    // Открытие модального окна для добавления нового сотрудника
    const handleOpenAddModal = () => {
        setIsModalOpen(true);
        setModalContent(
            <EmployeeForm onCloseModal={handleCloseModal} onAddEmployee={handleAddEmployee} />
        );
    };

    // Открытие модального окна для редактирования существующего сотрудника
    const handleOpenEditModal = (employee) => {
        setIsModalOpen(true);
        setSelectedEmployee(employee);
        setModalContent(
            <EmployeeEdit
                employeeData={employee}
                onUpdateEmployee={handleUpdateEmployee}
                onCloseModal={handleCloseModal}
            />
        );
    };

    // Открытие модального окна для просмотра деталей сотрудника
    const handleOpenDetailsModal = (employee) => {
        setIsModalOpen(true);
        setSelectedEmployee(employee);
        setModalContent(
            <Popup isOpen={true} onClose={handleCloseModal}>
                <EmployeeDetails employee={employee} />
            </Popup>
        );
    };

    // Открытие модального окна для удаления сотрудника
    const handleOpenDeleteModal = (employee) => {
        setIsModalOpen(true);
        setSelectedEmployee(employee);
        setModalContent(
            <EmployeeDelete
                employee={employee}
                onDeleteEmployee={handleConfirmDelete}
                onCloseModal={handleCloseModal}
            />
        );
    };

    // Закрытие модального окна
    const handleCloseModal = () => {
        setIsModalOpen(false);
        setSelectedEmployee(null);
        setModalContent(null);
    };

    const handleAddEmployee = (employeeData) => {
        setEmployees([...employees, employeeData]);
        handleCloseModal();
    };

    const handleUpdateEmployee = (employeeData) => {
        const updatedEmployees = employees.map(employee => {
            if (employee.id === employeeData.id) {
                return employeeData;
            }
            return employee;
        });
        setEmployees(updatedEmployees);
        handleCloseModal();
    };

    const handleConfirmDelete = () => {
        const updatedEmployees = employees.filter(emp => emp.id !== selectedEmployee.id);
        setEmployees(updatedEmployees);
        handleCloseModal();
    };

    return (
        <div>
            <div style={{ marginBottom: '10px' }}>
            </div>

            {/* Модальное окно */}
            {isModalOpen && modalContent}

            {/* Таблица с данными о сотрудниках */}
            <table className="employee-table">
                <thead>
                    <tr>
                        <th>Имя</th>
                        <th>Должность</th>
                        <th>Допуск</th>
                        <th>Действие</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((employee, index) => (
                        <tr key={index}>
                            <td>{employee.name}</td>
                            <td>{employee.position}</td>
                            <td>{employee.access}</td>
                            <td>
                                <button className="button" onClick={() => handleOpenDetailsModal(employee)}>Детали</button>
                                <button className="button" onClick={() => handleOpenEditModal(employee)}>Редактировать</button>
                                <button className="button" onClick={() => handleOpenDeleteModal(employee)}>Удалить</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Кнопка "Добавить" */}
            <div className="containeradd text-center">
                <Fab color="primary" aria-label="add" onClick={handleOpenAddModal}>
                    <AddIcon />
                </Fab>
            </div>
        </div>
    );
}

export default Accounting;
