import React, { useState } from 'react';
import Button from '@mui/material/Button';

function EmployeeDelete({ employee, onDeleteEmployee }) {
    const [isOpen, setIsOpen] = useState(false);

    const handleDelete = () => {
        onDeleteEmployee(employee);
        setIsOpen(false); // Закрываем модальное окно после удаления
    };

    const handleClose = () => {
        setIsOpen(false); // Закрываем модальное окно без удаления
    };

    const handleOpen = () => {
        setIsOpen(true); // Открываем модальное окно для подтверждения удаления
    };

    return (
        <div>
            <Button variant="contained" color="primary" onClick={handleOpen}>
                Удалить
            </Button>
            {isOpen && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>Вы уверены, что хотите удалить {employee.name}?</h2>
                        <Button variant="contained" color="primary" onClick={handleDelete}>
                            Да
                        </Button>
                        <Button variant="contained" color="secondary" onClick={handleClose}>
                            Нет
                        </Button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default EmployeeDelete;
