import React, { useState } from 'react';
import { makeStyles } from '@mui/styles';

import { Button, TextField, Grid, Paper } from '@mui/material';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
  },
  modalContent: {
    padding: theme.spacing(2),
    width: 400,
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    borderRadius: theme.shape.borderRadius,
  },
}));

function EmployeeForm({ onCloseModal, onAddEmployee }) {
  const classes = useStyles();
  const [employee, setEmployee] = useState({ name: '', position: '', access: '', birthdate: '', phone: '', address: '' });

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddEmployee(employee);
    onCloseModal();
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployee({ ...employee, [name]: value });
  };

  return (
    <div className={classes.modal}>
      <Paper className={classes.modalContent}>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Имя"
                name="name"
                value={employee.name}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Должность"
                name="position"
                value={employee.position}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Допуск"
                name="access"
                value={employee.access}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="День рождения"
                name="birthdate"
                value={employee.birthdate}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Телефон"
                name="phone"
                value={employee.phone}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Адрес"
                name="address"
                value={employee.address}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <Button type="submit" variant="contained" color="primary">
                Добавить
              </Button>
              <Button type="button" variant="contained" onClick={onCloseModal}>
                Отмена
              </Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
    </div>
  );
}

export default EmployeeForm;
