// Импортируем React и useState
import React, { useState } from 'react';
// Импортируем необходимые компоненты из библиотеки Material UI
import { TextField } from '@mui/material';
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';
import VisibilityIcon from '@mui/icons-material/Visibility';
// Импортируем компоненты для работы с сотрудниками
import EmployeeForm from './employeeForm';
import EmployeeEdit from './employeeEdit';
import EmployeeDetails from './employeeFormDetails'; 
import PopupComponent from './Popup'; // Поправленный импорт
// Импортируем начальные данные сотрудников
import data from '../data.json';
// Импортируем стили для компонента Accounting
import '../styles/accounting.css';

function Accounting() {
    // Состояния для управления видимостью форм и деталей сотрудника
    const [isFormVisible, setIsFormVisible] = useState(false);
    const [isEditFormVisible, setIsEditFormVisible] = useState(false);
    const [isDetailsVisible, setIsDetailsVisible] = useState(false);
    // Состояние для хранения списка сотрудников
    const [employees, setEmployees] = useState(data);
    // Состояния для поиска и сортировки
    const [searchQuery, setSearchQuery] = useState('');
    const [sortBy, setSortBy] = useState('name');
    // Состояние для хранения выбранного сотрудника
    const [selectedEmployee, setSelectedEmployee] = useState(null);

    // Функция для открытия формы добавления сотрудника
    const handleOpenForm = () => {
        setIsFormVisible(true);
    };

    // Функция для закрытия формы добавления сотрудника
    const handleCloseForm = () => {
        setIsFormVisible(false);
    };

    // Функция для открытия формы редактирования сотрудника
    const handleOpenEditForm = (employee) => {
        setSelectedEmployee(employee);
        setIsEditFormVisible(true);
    };

    // Функция для закрытия формы редактирования сотрудника
    const handleCloseEditForm = () => {
        setSelectedEmployee(null);
        setIsEditFormVisible(false);
    };

    // Функция для открытия деталей сотрудника
    const handleOpenDetails = (employee) => {
        setSelectedEmployee(employee);
        setIsDetailsVisible(true);
    };

    // Функция для закрытия деталей сотрудника
    const handleCloseDetails = () => {
        setIsDetailsVisible(false);
    };

    // Функция для поиска сотрудников
    const handleSearch = (event) => {
        setSearchQuery(event.target.value);
    };

    // Функция для сортировки сотрудников
    const handleSortChange = (event) => {
        setSortBy(event.target.value);
    };

    // Функция для обновления данных сотрудника после редактирования
    const handleUpdateEmployee = (updatedEmployeeData) => {
        const updatedEmployees = employees.map((employee) => {
            if (employee.id === updatedEmployeeData.id) {
                return updatedEmployeeData;
            } else {
                return employee;
            }
        });
        setEmployees(updatedEmployees);
        setIsEditFormVisible(false);
    };

    // Функция для добавления нового сотрудника
    const handleAddEmployee = (newEmployeeData) => {
        setEmployees([...employees, newEmployeeData]);
        setIsFormVisible(false);
    };

    // Функция для удаления сотрудника
    const handleDeleteEmployee = (employeeId) => {
        const updatedEmployees = employees.filter(employee => employee.id !== employeeId);
        setEmployees(updatedEmployees);
    };

    return (
        <div>
            {/* Компонент Popup для управления отображением модальных окон */}
            <PopupComponent isOpen={isFormVisible || isEditFormVisible || isDetailsVisible} onClose={() => { handleCloseForm(); handleCloseEditForm(); handleCloseDetails(); }}>
                {isFormVisible && (
                    <EmployeeForm onCloseForm={handleCloseForm} onAddEmployee={handleAddEmployee} />
                )}
                {isEditFormVisible && selectedEmployee && (
                    <EmployeeEdit 
                        employeeData={selectedEmployee} 
                        onUpdateEmployee={handleUpdateEmployee} 
                        onCloseModal={handleCloseEditForm} 
                    />
                )}
                {isDetailsVisible && selectedEmployee && (
                    <EmployeeDetails employee={selectedEmployee} isDetailsVisible={isDetailsVisible} handleCloseDetails={handleCloseDetails} />
                )}
            </PopupComponent>

            <div style={{ marginBottom: '10px' }}>
                <TextField
                    id="search"
                    label="Поиск"
                    variant="outlined"
                    value={searchQuery}
                    onChange={handleSearch}
                />
                <label>
                    Сортировать по:
                    <select value={sortBy} onChange={handleSortChange}>
                        <option value="name">Имени</option>
                    </select>
                </label>
            </div>

            <table className="employee-table">
                <thead>
                    <tr>
                        <th>Имя</th>
                        <th>Должность</th>
                        <th>Допуск</th>
                        <th>Действие</th>
                    </tr>
                </thead>
                <tbody>
                    {/* Отображение списка сотрудников */}
                    {employees.map((employee, index) => (
                        <tr key={index}>
                            <td>{employee.name}</td>
                            <td>{employee.position}</td>
                            <td>{employee.access}</td>
                            <td>
                                {/* Кнопки для просмотра, редактирования и удаления сотрудника */}
                                <IconButton aria-label="details" onClick={() => handleOpenDetails(employee)}>
                                    <VisibilityIcon />
                                </IconButton>
                                <IconButton color="primary" aria-label="edit" onClick={() => handleOpenEditForm(employee)}>
                                    <EditIcon />
                                </IconButton>
                                <IconButton color="secondary" aria-label="delete" onClick={() => handleDeleteEmployee(employee.id)}>
                                    <DeleteIcon />
                                </IconButton>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <div className="containeradd text-center">
                <Fab color="primary" aria-label="add" onClick={handleOpenForm}>
                    <AddIcon />
                </Fab>
            </div>
        </div>
    );
}

export default Accounting;
