import React from 'react';
import Button from '@mui/material/Button';
import Modal from '@mui/material/Modal';
import Box from '@mui/material/Box';

function EmployeeDelete({ employee, onDeleteEmployee, onCloseModal }) {
    const handleDelete = () => {
        onDeleteEmployee(employee);
        onCloseModal();
    };

    return (
        <Modal
            open={true} // Показывать модальное окно всегда открытым
            onClose={onCloseModal}
            aria-labelledby="delete-employee-modal"
            aria-describedby="delete-employee-description"
        >
            <Box
                sx={{
                    position: 'absolute',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    width: 400,
                    bgcolor: 'background.paper',
                    border: '2px solid #000',
                    boxShadow: 24,
                    p: 4,
                }}
            >
                <h2 id="delete-employee-modal">Вы уверены, что хотите удалить {employee.name}?</h2>
                <Button variant="contained" color="primary" onClick={handleDelete}>
                    Удалить
                </Button>
                <Button variant="contained" color="secondary" onClick={onCloseModal}>
                    Отмена
                </Button>
            </Box>
        </Modal>
    );
}

export default EmployeeDelete;
