import React, { useState } from 'react';
import EmployeeForm from './employeeForm';
import EmployeeEdit from './employeeEdit';
import EmployeeDetails from './employeeFormDetails';
import EmployeeDelete from './employeeDelete';
import Popup from './Popup'; // Импортируем компонент Popup
import data from '../data.json';
import '../styles/accounting.css';
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';
import VisibilityIcon from '@mui/icons-material/Visibility';

function Accounting() {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
    const [isDetailsOpen, setIsDetailsOpen] = useState(false);
    const [isButtonVisible, setIsButtonVisible] = useState(true); // Добавляем состояние для управления видимостью кнопки "Добавить"
    const [employees, setEmployees] = useState(data);
    const [selectedEmployee, setSelectedEmployee] = useState(null); // Состояние для хранения выбранного сотрудника
    const [isAnyModalOpen, setIsAnyModalOpen] = useState(false); // Состояние для отслеживания открытых модальных окон

    const handleOpenModal = () => {
        setIsModalOpen(true);
        setIsAnyModalOpen(true); // Устанавливаем флаг, что открыто модальное окно
        setIsButtonVisible(false); // Скрываем кнопку "Добавить" при открытии модального окна
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setSelectedEmployee(null);
        setIsAnyModalOpen(false); // Устанавливаем флаг, что закрыто модальное окно
        setIsButtonVisible(true); // Показываем кнопку "Добавить" при закрытии модального окна
    };

    const handleEditModalOpen = () => {
        setIsEditModalOpen(true);
        setIsAnyModalOpen(true); // Устанавливаем флаг, что открыто модальное окно
    };

    const handleEditModalClose = () => {
        setIsEditModalOpen(false);
        setSelectedEmployee(null);
        setIsAnyModalOpen(false); // Устанавливаем флаг, что закрыто модальное окно
    };

    const handleAddEmployee = (employeeData) => {
        setEmployees([...employees, employeeData]);
        handleCloseModal();
    };

    const handleUpdateEmployee = (employeeData) => {
        const updatedEmployees = employees.map(employee => {
            if (employee.id === employeeData.id) {
                return employeeData;
            }
            return employee;
        });
        setEmployees(updatedEmployees);
        handleEditModalClose();
    };

    const handleEditEmployee = (employee) => {
        setSelectedEmployee(employee); // Установка выбранного сотрудника
        handleEditModalOpen();
    };

    const handleDeleteEmployee = (employee) => {
        setSelectedEmployee(employee); // Установка выбранного сотрудника
        setIsDeleteModalOpen(true);
        setIsAnyModalOpen(true); // Устанавливаем флаг, что открыто модальное окно
    };

    const handleConfirmDelete = () => {
        const updatedEmployees = employees.filter(emp => emp.id !== selectedEmployee.id);
        setEmployees(updatedEmployees);
        setIsDeleteModalOpen(false);
        setSelectedEmployee(null);
        setIsAnyModalOpen(false); // Устанавливаем флаг, что закрыто модальное окно
    };

    const handleShowDetails = (employee) => {
        setSelectedEmployee(employee); // Установка выбранного сотрудника
        setIsDetailsOpen(true);
        setIsAnyModalOpen(true); // Устанавливаем флаг, что открыто модальное окно
    };

    const handleCloseDetails = () => {
        setIsDetailsOpen(false);
        setSelectedEmployee(null);
        setIsAnyModalOpen(false); // Устанавливаем флаг, что закрыто модальное окно
    };

    return (
        <div>
            <div style={{ marginBottom: '10px' }}>
            </div>

            {/* Модальное окно для добавления нового сотрудника */}
            {isModalOpen && (
                <div className="modal-overlay">
                    <EmployeeForm onCloseModal={handleCloseModal} onAddEmployee={handleAddEmployee} />
                </div>
            )}

            {/* Модальное окно для редактирования существующего сотрудника */}
            {isEditModalOpen && selectedEmployee && (
                <div className="modal-overlay">
                    <EmployeeEdit
                        employeeData={selectedEmployee}
                        onUpdateEmployee={handleUpdateEmployee}
                        onCloseModal={handleEditModalClose}
                    />
                </div>
            )}

            {/* Модальное окно для удаления сотрудника */}
            {isDeleteModalOpen && selectedEmployee && (
                <div className="modal-overlay">
                    <EmployeeDelete
                        employee={selectedEmployee}
                        onDeleteEmployee={handleConfirmDelete}
                        onCloseModal={() => setIsDeleteModalOpen(false)}
                    />
                </div>
            )}

            {/* Модальное окно с деталями сотрудника */}
            {isDetailsOpen && selectedEmployee && (
                <div className="modal-overlay">
                    {/* Использование компонента EmployeeDetails с модальным окном Popup */}
                    <Popup isOpen={isDetailsOpen} onClose={handleCloseDetails}>
                        <EmployeeDetails employee={selectedEmployee} />
                    </Popup>
                </div>
            )}

            {/* Таблица с данными о сотрудниках */}
            <table className="employee-table">
                <thead>
                    <tr>
                        <th>Имя</th>
                        <th>Должность</th>
                        <th>Допуск</th>
                        <th>Действие</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((employee, index) => (
                        <tr key={index}>
                            <td>{employee.name}</td>
                            <td>{employee.position}</td>
                            <td>{employee.access}</td>
                            <td>
                                <IconButton aria-label="details" onClick={() => handleShowDetails(employee)} disabled={isAnyModalOpen}>
                                    <VisibilityIcon />
                                </IconButton>
                                <IconButton color="primary" aria-label="edit" onClick={() => handleEditEmployee(employee)} disabled={isAnyModalOpen}>
                                    <EditIcon />
                                </IconButton>
                                <IconButton color="secondary" aria-label="delete" onClick={() => handleDeleteEmployee(employee)} disabled={isAnyModalOpen}>
                                    <DeleteIcon />
                                </IconButton>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Кнопка "Добавить" */}
            <div className="containeradd text-center">
                {isButtonVisible && !isAnyModalOpen && ( // Показываем кнопку "Добавить" только если isButtonVisible равно true и нет открытых модальных окон
                    <Fab color="primary" aria-label="add" onClick={handleOpenModal}>
                        <AddIcon />
                    </Fab>
                )}
            </div>
        </div>
    );
}

export default Accounting;
