import React, { useState } from 'react';
import Button from '@mui/material/Button'; // Импорт компонента Button из Material-UI
import Box from '@mui/material/Box'; // Импорт компонента Box из Material-UI

function EmployeeDelete({ employee, onDeleteEmployee, onCloseModal }) {
    const [isDeleting, setIsDeleting] = useState(false); // Состояние для блокировки кнопки "Удалить"

    const handleDelete = () => {
        setIsDeleting(true); // Устанавливаем состояние "Удаление" в true
        onDeleteEmployee(employee);
        onCloseModal();
    };

    const handleCancel = () => {
        onCloseModal();
    };

    return (
        <div className="modal">
            <div className="modal-content">
                <h2>Удаление сотрудника</h2>
                <p>Вы уверены, что хотите удалить сотрудника {employee.name}?</p>
                <Box sx={{ display: 'flex', justifyContent: 'center', gap: '28px' }}>
                    {/* Кнопка "Удалить" */}
                    <Button variant="contained" color="error" onClick={handleDelete} disabled={isDeleting}>
                        {isDeleting ? 'Удаление...' : 'Удалить'}
                    </Button>
                    {/* Кнопка "Отмена" */}
                    <Button variant="contained" onClick={handleCancel} disabled={isDeleting}>
                        Отмена
                    </Button>
                </Box>
            </div>
        </div>
    );
}

export default EmployeeDelete;
