import React, { useState } from 'react';
import { Button, TextField, Box } from '@mui/material';
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';
import VisibilityIcon from '@mui/icons-material/Visibility';
import EmployeeForm from './employeeForm';
import EmployeeEdit from './employeeEdit';
import EmployeeAdd from './employeeAdd'; // Импортируем компонент добавления сотрудника
import data from '../data.json';
import '../styles/accounting.css';

function Accounting() {
    const [isFormVisible, setIsFormVisible] = useState(false);
    const [isEditFormVisible, setIsEditFormVisible] = useState(false);
    const [employees, setEmployees] = useState(data);
    const [searchQuery, setSearchQuery] = useState('');
    const [sortBy, setSortBy] = useState('name');
    const [selectedEmployee, setSelectedEmployee] = useState(null);

    const handleOpenForm = () => {
        setIsFormVisible(true);
    };

    const handleCloseForm = () => {
        setIsFormVisible(false);
    };

    const handleOpenEditForm = (employee) => {
        setSelectedEmployee(employee);
        setIsEditFormVisible(true);
    };

    const handleCloseEditForm = () => {
        setSelectedEmployee(null);
        setIsEditFormVisible(false);
    };

    const handleSearch = (event) => {
        setSearchQuery(event.target.value);
    };

    const handleSortChange = (event) => {
        setSortBy(event.target.value);
    };

    const handleUpdateEmployee = (updatedEmployeeData) => {
        const updatedEmployees = employees.map((employee) => {
            if (employee.id === updatedEmployeeData.id) {
                return updatedEmployeeData;
            } else {
                return employee;
            }
        });
        setEmployees(updatedEmployees);
        setIsEditFormVisible(false);
    };

    // Функция для добавления нового сотрудника
    const handleAddEmployee = (newEmployee) => {
        // Генерация уникального идентификатора для нового сотрудника
        newEmployee.id = Math.random().toString(36).substr(2, 9);
        setEmployees([...employees, newEmployee]);
    };

    return (
        <div>
            {isFormVisible && (
                <EmployeeForm onCloseForm={handleCloseForm} />
            )}

            {isEditFormVisible && selectedEmployee && (
                <EmployeeEdit
                    employeeData={selectedEmployee}
                    onUpdateEmployee={handleUpdateEmployee}
                    onCloseModal={handleCloseEditForm}
                />
            )}

            <EmployeeAdd onAddEmployee={handleAddEmployee} /> {/* Компонент для добавления сотрудника */}

            <div style={{ marginBottom: '10px' }}>
                <TextField
                    id="search"
                    label="Поиск"
                    variant="outlined"
                    value={searchQuery}
                    onChange={handleSearch}
                />
                <label>
                    Сортировать по:
                    <select value={sortBy} onChange={handleSortChange}>
                        <option value="name">Имени</option>
                    </select>
                </label>
            </div>

            <table className="employee-table">
                <thead>
                    <tr>
                        <th>Имя</th>
                        <th>Должность</th>
                        <th>Допуск</th>
                        <th>Действие</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((employee, index) => (
                        <tr key={index}>
                            <td>{employee.name}</td>
                            <td>{employee.position}</td>
                            <td>{employee.access}</td>
                            <td>
                                <IconButton aria-label="details">
                                    <VisibilityIcon />
                                </IconButton>
                                <IconButton color="primary" aria-label="edit" onClick={() => handleOpenEditForm(employee)}>
                                    <EditIcon />
                                </IconButton>
                                <IconButton color="secondary" aria-label="delete">
                                    <DeleteIcon />
                                </IconButton>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <div className="containeradd text-center">
                <Fab color="primary" aria-label="add" onClick={handleOpenForm}>
                    <AddIcon />
                </Fab>
            </div>
        </div>
    );
}

export default Accounting;
