import React from 'react';
import Button from '@mui/material/Button';
import '../styles/modal.css'; // Импорт файла стилей для модального окна

function EmployeeDelete({ employee, onDeleteEmployee, onCloseModal }) {
    const handleDelete = () => {
        onDeleteEmployee(employee);
    };

    const handleClose = () => {
        onCloseModal();
    };

    return (
        <div className="modal"> {/* Обертка для модального окна */}
            <div className="modal-content">
                <h2>Вы уверены, что хотите удалить сотрудника {employee.name}?</h2>
                <div className="modal-actions">
                    <Button onClick={handleDelete} variant="contained" color="error">
                        Удалить
                    </Button>
                    <Button onClick={() => {
                        handleClose();
                        setIsAnyModalOpen(false); // Обновляем состояние isAnyModalOpen
                        setIsDeleteModalOpen(false); // Обновляем состояние isDeleteModalOpen
                    }} variant="contained">
                        Отмена
                    </Button>
                </div>
            </div>
        </div>
    );
}

export default EmployeeDelete;
